mapper.select:
SELECT t_order_op_his.id, t_order_op_his.oper_log,t_order.order_sn as order_sn,t_user.user_id as user_id,t_order_op_his.update_time FROM t_order_op_his,t_order,t_user WHERE (t_order_op_his.order_idx = t_order.id and t_order_op_his.oper_idx = t_user.id)
AND t_order_op_his.order_idx = (select id from t_order where order_sn = #{orderSn})
AND t_order_op_his.oper_idx = (select id from t_user where user_id = #{userId})
ORDER BY ${orderBy}
limit #{limit} offset #{offset};
