mapper.sql:
select department_id,department_name,department_code,spec_id,spec_name,spec_code, sum(coupon_price) as sum,count(coupon_id) as count FROM ( select t_department.id as department_id, t_department.name as department_name, t_department.code as department_code, t_goods.id as spec_id,t_goods.name as spec_name,t_goods.code as spec_code, t_coupon.price as coupon_price,t_coupon.id as coupon_id from t_coupon,t_coupon_order,t_order ,t_order_detail,t_order_dispatcher,t_department,t_sysuser,t_goods where t_order_detail.order_idx = t_order.id and t_order_detail.goods_idx = t_goods.id and t_order.id = t_order_dispatcher.order_idx and t_order_dispatcher.dispatcher_idx = t_sysuser.user_idx and t_sysuser.department_idx = t_department.id and t_coupon_order.order_idx = t_order.id and t_coupon_order.coupon_idx = t_coupon.id
and t_order.pay_type = 4
) b group by department_id,spec_id

select t_department.name as department_name, t_department.code as department_code, t_goods.name as spec_name,t_goods.code as spec_code, sum(t_order_detail.subtotal) as sum ,sum(t_order_detail.quantity) as count from t_order_detail,t_order ,t_order_dispatcher,t_department,t_sysuser,t_goods where t_order_detail.order_idx = t_order.id and t_order_detail.goods_idx = t_goods.id and t_order.id = t_order_dispatcher.order_idx and t_order_dispatcher.dispatcher_idx = t_sysuser.user_idx and t_sysuser.department_idx = t_department.id
and t_order.pay_type = #{payType}
group by t_department.id,t_goods.id

select department_id,department_name,department_code,spec_id,spec_name,spec_code, sum(ticket_price) as sum,count(ticket_id) as count FROM ( select t_department.id as department_id, t_department.name as department_name, t_department.code as department_code, t_goods.id as spec_id,t_goods.name as spec_name,t_goods.code as spec_code, t_ticket.price as ticket_price,t_ticket.id as ticket_id from t_ticket,t_ticket_order,t_order ,t_order_detail,t_order_dispatcher,t_department,t_sysuser,t_goods where t_order_detail.order_idx = t_order.id and t_order_detail.goods_idx = t_goods.id and t_order.id = t_order_dispatcher.order_idx and t_order_dispatcher.dispatcher_idx = t_sysuser.user_idx and t_sysuser.department_idx = t_department.id and t_ticket_order.order_idx = t_order.id and t_ticket_order.ticket_idx = t_ticket.id
and t_order.pay_type = #{payType}
) b group by department_id,spec_id
